export default {
    data() {
        return {
            isAdd: true, // 是否为新增分类 默认是
            tableList: [], // 表格数据源
            isModal: false, // 控制对话框的显示与隐藏
            formSource: {
                id: null, // 分类id
                class_name: '', // 分类名称
            }, // 表单数据
            rules: {
                class_name: [
                    { required: true, message: '请输入分类名称', trigger: 'blur' },
                    { min: 1, max: 6, message: '长度在 1 到 6 个字符', trigger: 'blur' },
                ],
            },
        }
    },
    created() {
        this.getClassList() // 获取分类
    },
    methods: {
        // 获取分类
        async getClassList() {
            const { data: res } = await this.$http.post('getClassTabs', {})
            if (res.status === 200) {
                this.tableList = res.data
            } else {
                this.$message.error(res.msg)
            }

        },
        // 打开弹框
        openModal(type, data) {
            this.isAdd = type
            this.isModal = true
            if (!type) {
                // 如果是编辑 那么拿一下编辑的内容
                this.formSource = JSON.parse(JSON.stringify(data))
            }
        },
        // 提交表单
        async submitForm() {
            this.$refs.formSourceRef.validate(async (valid) => {
                if (valid) {
                    await this.setClass();
                } else {
                    this.$message.error('请检查表单内容');
                }
            });
        },
        // 修改分类
        async setClass() {
            if (this.isAdd) {
                // 新增分类
                const { data: res } = await this.$http.post('addClassTabs', { class_name: this.formSource.class_name })
                if (res.status === 200) {
                    this.isModal = false
                    this.getClassList()
                } else {
                    this.$message.error(res.msg)
                }
            } else {
                // 修改分类
                const { data: res } = await this.$http.post('updateClassTabs', this.formSource)
                if (res.status === 200) {
                    this.isModal = false
                    this.getClassList()
                } else {
                    this.$message.error(res.msg)
                }
            }
        },
        // 删除分类
        removeClass(data) {
            this.$confirm('此操作将无法恢复, 是否继续?', '提示', {
                confirmButtonText: '确定',
                cancelButtonText: '取消',
                type: 'warning',
            })
                .then(async () => {
                    const { data: res } = await this.$http.post('delClassTabs', { id: data.id })
                    if (res.status === 200) {
                        this.getClassList()
                    } else {
                        this.$message.error(res.msg)
                    }
                })
                .catch(() => {})
        },

        // 关闭修改对话框
        closeModal() {
            this.formSource = {
                id: null, // 分类id
                class_name: '', // 分类名称
            }
            this.$refs.formSourceRef.resetFields()
        },
    },
}
